using System;
using System.Windows.Forms;
using Microsoft.Practices.CompositeUI.SmartParts;
using Microsoft.Practices.ObjectBuilder;
using Sante.EMR.SmartClient.Infrastructure.Interface;
using Sante.EMR.SmartClient.Infrastructure.Interface.BusinessEntities;
using Sante.EMR.SmartClient.Infrastructure.Interface.View;
using Microsoft.Practices.CompositeUI;
using Sante.EMR.SmartClient.Infrastructure.Logger.Services;
using Sante.EMR.SmartClient.Module.Services;
using Sante.EMR.SmartClient.MCT.Services;

namespace Sante.EMR.SmartClient.OPS
{
    public partial class NewLethalityFormView : FormView, INewLethalityFormView
    {
        IOutboxService _outbox;
        ILoggerService _logger;
        IWorkflowStateService _state;
        public NewLethalityFormView([ServiceDependency] ILoggerService logger,
                                    [ServiceDependency] IOutboxService outbox,
                                    [ServiceDependency] IWorkflowStateService state)
        {
            InitializeComponent();
            _form = new Lethality();
            this.Date.Text = _date.ToShortDateString();
            this.Time.Text = _time.ToShortTimeString();
            _logger = logger;
            _state = state;
        }

        protected override void OnLoad(EventArgs e)
        {
            _presenter.OnViewReady();
            base.OnLoad(e);
        }

        private void SaveToolStripButton_Click(object sender, EventArgs e)
        {
            _form.SetFormFields(this.Controls);
            _form.tstamp = DateTime.Now;
            _form.Depart = _state.Depart;
            _form.FormName = "Lethality Form";
            _presenter.SaveForm(_form);
            _presenter.ShowMsg("The Lethality form was successfully saved.");
            _presenter.OnCloseView();

                
            
                
        }

        private void NewLethalityFormView_Load(object sender, EventArgs e)
        {

        }

        
    }
}

